import {useState} from 'react';
import './App.css';
<link
  rel="stylesheet"
  href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/css/bootstrap.min.css"
  integrity="sha384-T3c6CoIi6uLrA9TneNEoa7RxnatzjcDSCmG1MXxSR1GAsXEV/Dwwykc2MPK8M2HN"
  crossorigin="anonymous"
/>

function App() {
  const [rows,setRows] = useState(0);
  const [cols, setCols] = useState(0);

  return (
    <div className="App">
      <div class="container mt-5">
        <form action="/generate" method="post">
            <div class="form-group">
                <label for="rows">Enter rows:</label>
                <input type="number" class="form-control" id="rows" name="rows" value= {rows} min="0" max="100" required />
            </div>

            <div class="form-group">
                <label for="columns">Columns:</label>
                <input type="number" class="form-control" id="columns" name="columns" value= {cols} min="0" max="100" required />
            </div>
            <button type="submit" class="btn btn-primary">Generate</button>
        </form>
      </div>
    </div>
  );
}

export default App;
